import React ,{useState}from 'react'

const Addcart = () => {

    const [addcart, setAddCart] = useState([]);

    const addCart=(item)=>{
setAddCart([...addCart, item])
    }

    const remove=(item)=>{
        const newItem = [...addCart];
        newItem.splice(item,1);
        setAddCart(newItem);
    }

  return (
    <>
    <div className="shop-cart">
        <h1>Add-To-Cart</h1>
        
        <div className='add-item'>
        <input type="text" id="itemInput" />
        <button onClick={() => addcart(document.getElementById('itemInput').value)}>Add to Cart</button>
      </div>
    </div>
    
    </>
  )
}

export default Addcart
