import React, { useContext,useState ,useEffect} from 'react';
import { CartContext } from './CartContext';
import "./style.css"
const Cart = () => {
    const [totalitem ,setTotalItem] =useState(0)
  const { cartItems, removeFromCart, getSubtotal, } = useContext(CartContext);

  const handleRemoveFromCart = (item) => {
    removeFromCart(item);
  };

  useEffect(()=>{
    setTotalItem(cartItems.filter(cartItems=> cartItems).length)
  })

  return (
    <div className='cart-container' >
      <h2>Cart</h2>
      <span style={{fontSize:"20px"}}>Add-Items:({totalitem})</span>
      {cartItems.length === 0 ? (
        <p style={{fontSize:"15px"}}>Your cart is empty.</p>
      ) : (
        <>
          <div>
            {cartItems.map((item) => (
              <li key={item.id}>
                {item.name} - ${item.price} x {item.quantity}
                <button onClick={() => handleRemoveFromCart(item)} style={{marginTop:"5px"}}>Remove</button>
              </li>
            ))}
          </div>
          <p>Subtotal: ${getSubtotal()}</p>
        </>
      )}
    </div>
  );
};

export default Cart;
