import React from 'react'

const Task = ({task,index,completeTask,removeTask}) => {
  return (
    <>
    <div className="task" style={{textDecoration:task.completed ? "line-through": " "}}>
      {task.title}
      <button onClick={()=>completeTask(index)} style={{marginLeft:""}}>Complete</button>
      <button onClick={()=>removeTask(index)} style={{backgroundColor:"red",marginLeft:"2px",textAlign:"center"}} >x</button>
    </div>
    </>
  )
}

export default Task
