import React, { useContext } from 'react';
import { CartContext } from "./CartContext"


// let's create a component to display and interact with the cart
const Cart = () => {

    // use the useContext hook to access the cart items, removeFromCart function from the CartContext.
  const { cartItems, removeFromCart,addToCart } = useContext(CartContext);

  return (
    <div>
      <h2>Shopping Cart</h2>
      {/* here use ternsry method for display the cart items in a list and provide a "Remove" button for each item. */}
      {cartItems.length === 0 ? (
        <div className="addItem">
            
            <input type="text"/>
            <button onClick={()=>addToCart()}>Add</button>
        </div>
      ) : (
        <ul>
          {cartItems.map((item) => (
            <li key={item.id}>
              {item.name}
              <button onClick={() => removeFromCart(item.id)}>Remove</button>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default Cart;
