import React, { useEffect } from 'react'
import { useState } from 'react'
import Task from './Task'
import CreateTask from './CreateTask'

const Todo = () => {


  const [taskdone, setTaskDone] = useState(0)
  const[totaltask,setTotalTask]=useSate
  const [task, setTask] = useState([
    {
      title: "Grab some Pizza",
      completed: true
  },
  {
      title: "Do your workout",
      completed: true
  },
  {
      title: "Hangout with friends",
      completed: false
  }
  ]);

  useEffect(()=>{
    
    setTaskDone(task.filter(task => task.completed).length)
  })

// add new task 
  const addTask = title => {
    const newTasks = [...task, { title, completed: false }];
    setTask(newTasks);
};

const completeTask = index => {
  const newTasks = [...task];
  newTasks[index].completed = true;
  setTask(newTasks);
};

const removeTask = index => {
  const newTasks = [...task];
  newTasks.splice(index, 1);
  setTask(newTasks);
};

  return (
   <>
   <div className='todo-list'>
    <h1 >To-D<span style={{color:"orange"}}>o-List</span></h1>
    <span>Total-Task-Done:({taskdone})</span>
    <span>Total-Task:</span>
   

    <div className="card">
      {task.map((task,index)=>(
        <Task task={task}
        index={index}
        completeTask={completeTask}
        removeTask={removeTask}
        key={index}/>
      ))}

    </div>
    <div className="creatTask">
      <CreateTask addTask={addTask}/>
    </div>
   </div>
   </>
  )
}

export default Todo;
